settings.cardCss:
.card {
   width: 100%;
   height: 100%;
   border: 2px solid #ccc;
   box-sizing: border-box;
}

.card .content {
   padding: 20px;
   display: flex;
   flex-direction: row;
   align-items: center;
   justify-content: space-around;
   height: 100%;
   box-sizing: border-box;
}

.card .content .column {
   display: flex;
   flex-direction: column;    
   justify-content: space-around;
   height: 100%;
}

.card h1 {
    text-transform: uppercase;
    color: #999;
    font-size: 20px;
    font-weight: bold;
    margin: 0;
    padding-bottom: 10px;
    line-height: 32px;
}

.card .value {
    font-size: 38px;
    font-weight: 200;
}

.card .description {
    font-size: 20px;
    color: #999;
}

settings.markdownCss:
.header {
    height: 100%;
    padding: 0 0 0 24px;
} 

.header .mat-icon.title-icon {
    width: 40px;
    height: 40px;
}

.header .title {
    font-size: 14px;
    font-weight: 500;
    color: #28232D;
    margin-left: 25px;
}

.header .subtitle {
    font-size: 13px;
    font-weight: normal;
    color: #757575;
}
.body {
